error PredictionMarket_NotOracle();

contract PredictionMarket {
    /* Types */

    // Which party will win the 2024 Election?
    enum Party {
        Democratic,
        Republican
    }

    enum EventState {
        ONGOING,
        FINISHED
    }

    struct Result {
        Party winner;
        Party loser;
    }

    /* State variable */
    Result public s_result;

